for noticia in noticias:
    estados[noticia.get_estado().get_uf()].set_noticia(noticia)
curtidas = {}
lista_noticias = noticias[:]


def bubble_sort(lista):
    for i in range(len(lista)):
        for j in range(len(lista) - 1):
            if lista[j].get_curtidas() < lista[j + 1].get_curtidas():
                lista[j], lista[j + 1] = lista[j + 1], lista[j]
    return lista


@app.route("/")
def home():
    for noticia in noticias:
        curtidas[noticia.get_id()] = noticia.get_curtidas()
        # maisCurtidas = sorted(curtidas.items(), key=lambda x: x[1], reverse=True)
    maisCurtidas = bubble_sort(lista_noticias)
    return render_template("home.html", estados=estados, noticias=noticias, maisCurtidas=maisCurtidas)


@app.route("/estado/<string:uf>")
def noticias_do_estado(uf):
    for estado in estados:
        if estados[estado].get_uf() == uf.upper() and estados[estado].get_noticias() != []:
            return render_template("noticias-estado.html", noticias=estados[estado].get_noticias(), estados=estados)
    return render_template("home.html", estados=estados, noticias=noticias)


@app.route("/noticia/<int:id>")
def noticia(id):
    for noticia in noticias:
        if noticia.get_id() == id:
            return render_template("noticia.html", noticia=noticia, estados=estados, curtidas=noticia.get_curtidas())
    return render_template("home.html", estados=estados, noticias=noticias)


@app.route("/noticia/like/<int:id>")
def curtida(id):
    for noticia in noticias:
        if noticia.get_id() == id:
            noticia.add_curtida()
            return redirect(url_for("noticia", id=id))
